from utils.cryptography.analyze import ngrams, replace_by_dict
from utils.morseCode import from_morse_code

s = ""

for i in range(1,5):
    print(ngrams(s,i))

to_morse = {
    'A': '.',
    'B': '-',
    'C': '_',
    'D': ' '
}
s = replace_by_dict(s, to_morse)
print(s)
print(from_morse_code(s, escaped=' _'))